mapper.insert:
INSERT INTO course (id, name, dept_id,
                    price, icon, pid, details,
                    description, pids, number,point_id
)
VALUES (#{course.id}, #{course.name}, #{course.deptId}, #{course.price}, #{course.icon}, #{course.pid},
        #{course.details},
        #{course.description}, #{course.pids}, #{course.number}, #{course.pointId}
)

mapper.select:
select

from course


        and name = #{course.name}


        and `id` = #{course.id}


        and dept_id = #{course.deptId}


        and price = #{course.price}


        and pid = #{course.pid}


        and pids LIKE CONCAT('%', '[${course.pids}]','%' )


        and number = #{course.number}


        and price <= #{endPrice}


        and price >= #{openPrice}


        and `point_id` = #{course.pointId}


ORDER BY ${sort} ${order}
LIMIT #{offset}, #{limit};;

select count(*)
from course


        and `point_id` = #{course.pointId}


        and name = #{course.name}


        and `id` = #{course.id}


        and dept_id = #{course.deptId}


        and price = #{course.price}


        and pid = #{course.pid}


        and pids LIKE CONCAT('%', '[${course.pids}]','%' )


        and number = #{course.number}


        and price <= #{endPrice}


        and price >= #{openPrice}

;
