mapper.select:
SELECT

FROM shopping_cart
WHERE id = #{id}
ORDER BY ${sort} ${order}
LIMIT #{offset}, #{limit}

SELECT count(id)
FROM shopping_cart
WHERE id = #{principal}

SELECT

FROM shopping_cart
WHERE user_id = #{shoppingCart.userId}
AND resource_id = #{shoppingCart.resourceId}
AND type #{shoppingCart.type}
