mapper.select:
SELECT

FROM user
WHERE 1=1

    AND id = #{id,jdbcType=INTEGER}


    AND email = #{email,jdbcType=VARCHAR}


    AND phone = #{phone,jdbcType=VARCHAR}


    AND idCard = #{idCard,jdbcType=VARCHAR}

SELECT

FROM role r
JOIN user_role ur ON r.id=ur.role_id
JOIN user u ON ur.user_id=u.id
WHERE 1=1

    AND id = #{id,jdbcType=INTEGER}


    AND email = #{email,jdbcType=VARCHAR}


    AND phone = #{phone,jdbcType=VARCHAR}


    AND idCard = #{idCard,jdbcType=VARCHAR}

mapper.update:
UPDATE user
SET dept_id = #{deptId}
WHERE id = #{userId}
